  CONSTRAINT `request_adopt_id` FOREIGN KEY (`adopt_id`) REFERENCES `adopt` (`adopt_id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `request_agency_id` FOREIGN KEY (`agency_id`) REFERENCES `agency` (`agency_id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `request_user_id` FOREIGN KEY (`user_openid`) REFERENCES `user` (`open_id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=10 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

